<!doctype html><html><head>	<link rel="stylesheet" href="styles.css" type="text/css"></head><body>	<div id="login">		<?php if(!empty($message)) : ?>			<div class="alert">				<?php echo $message; ?>			</div>		<?php endif; ?>		<?php if(first_time()) : ?>			<h1>Enter a Password for First Time Use</h1>			<h4>This Password will be saved, and to access this page again, you will need to re-enter it.  Please write down adn remember your password.</h4>		<?php else : ?>			<h1>Enter Valid Password</h1>		<?php endif; ?>		<form action="" method="post">			<table>				<tr>					<th><label for="password">Password</label></th>					<td><input type="password" name="password" /></td>				</tr>				<tr>					<th>&nbsp;</th>					<td><input type="submit" value="Submit" /></td>				</tr>			</table>		</form>	</div></body></html>