$(window).on('load', function(){
	$('.preloader').delay(1000).fadeOut('slow');
	scaleVideoContainer();
	initBannerVideoSize('.hero-video__container .poster img');
	//initBannerVideoSize('.video-container .filter');
	initBannerVideoSize('.hero-video__container video');
});
$(window).on('resize', function() {
	scaleVideoContainer();
	scaleBannerVideoSize('.hero-video__container .poster img');
	//scaleBannerVideoSize('.video-container .filter');
	scaleBannerVideoSize('.hero-video__container video');
});

function scaleVideoContainer() {

    var height = $(window).height();
    var unitHeight = parseInt(height) + 'px';
    $('.hero-video').css('height',unitHeight);

}
function initBannerVideoSize(element){
    
    $(element).each(function(){
        $(this).data('height', $(this).height());
        $(this).data('width', $(this).width());
    });

    scaleBannerVideoSize(element);

}
function scaleBannerVideoSize(element){

    var windowWidth = $(window).width(),
        windowHeight = $(window).height(),
        videoWidth,
        videoHeight;
    
    console.log(windowHeight);

    $(element).each(function(){
        var videoAspectRatio = $(this).data('height')/$(this).data('width'),
            windowAspectRatio = windowHeight/windowWidth;

        if (videoAspectRatio > windowAspectRatio) {
            videoWidth = windowWidth;
            videoHeight = videoWidth * videoAspectRatio;
            $(this).css({'top' : -(videoHeight - windowHeight) / 2 + 'px', 'margin-left' : 0});
        } else {
            videoHeight = windowHeight;
            videoWidth = videoHeight / videoAspectRatio;
            $(this).css({'margin-top' : 0, 'margin-left' : -(videoWidth - windowWidth) / 2 + 'px'});
        }

        $(this).width(videoWidth).height(videoHeight);

        $('.hero-video .hero-video__container').addClass('fadeIn animated');
        

    });
}
